	@Test public void testDouble() {
		String[][] testCases = {
			{"5DF6E0E2761359D30A8275058E299FCC0381534545F55CF43E41983F5D4C9456", ""},
			{"4F8B42C22DD3729B519BA6F68D2DA7CC5B2D606D05DAED5AD5128CC03E6C6358", "abc"},
			{"0CFFE17F68954DAC3A84FB1458BD5EC99209449749B2B308B7CB55812F9563AF", "abcdbcdecdefdefgefghfghighijhijkijkljklmklmnlmnomnopnopq"},
		};
		for (String[] testCase : testCases)
			assertArrayEquals(Utils.hexToBytes(testCase[0]), Sha256.getDoubleHash(Utils.asciiToBytes(testCase[1])).toBytes());
	}
	
	
	@Test public void testHmac() {
		Object[][] testCases = {  // Each entry has type (String, byte[], byte[])
			{"B0344C61D8DB38535CA8AFCEAF0BF12B881DC200C9833DA726E9376C2E32CFF7", Utils.hexToBytes("0B0B0B0B0B0B0B0B0B0B0B0B0B0B0B0B0B0B0B0B"), Utils.asciiToBytes("Hi There")},
			{"5BDCC146BF60754E6A042426089575C75A003F089D2739839DEC58B964EC3843", Utils.asciiToBytes("Jefe"), Utils.asciiToBytes("what do ya want for nothing?")},
			{"773EA91E36800E46854DB8EBD09181A72959098B3EF8C122D9635514CED565FE", Utils.hexToBytes("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"), Utils.hexToBytes("DDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDDD")},
			{"82558A389A443C0EA4CC819899F2083A85F0FAA3E578F8077A2E3FF46729665B", Utils.hexToBytes("0102030405060708090A0B0C0D0E0F10111213141516171819"), Utils.hexToBytes("CDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCDCD")},
			{"A3B6167473100EE06E0C796C2955552BFA6F7C0A6A8AEF8B93F860AAB0CD20C5", Utils.hexToBytes("0C0C0C0C0C0C0C0C0C0C0C0C0C0C0C0C0C0C0C0C"), Utils.asciiToBytes("Test With Truncation")},
			{"60E431591EE0B67F0D8A26AACBF5B77F8E0BC6213728C5140546040F0EE37F54", Utils.hexToBytes("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"), Utils.asciiToBytes("Test Using Larger Than Block-Size Key - Hash Key First")},
			{"9B09FFA71B942FCB27635FBCD5B0E944BFDC63644F0713938A7F51535C3A35E2", Utils.hexToBytes("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA"), Utils.asciiToBytes("This is a test using a larger than block-size key and a larger than block-size data. The key needs to be hashed before being used by the HMAC algorithm.")},
		};
		for (Object[] testCase : testCases)
			assertArrayEquals(Utils.hexToBytes((String)testCase[0]), Sha256.getHmac((byte[])testCase[1], (byte[])testCase[2]).toBytes());
	}
	
